mapper.select:
select ,difdays,startpulltimeDateFormat from
(select ,DATE_FORMAT(startpulltime,'%Y-%m-%d') startpulltimeDateFormat,DATEDIFF(now(),startpulltime) as difdays from test) temp
where (downflag=#{downflag} or difdays>=#{intervalDay}) and deleteflag=#{deleteflag}

mapper.update:
update test

  deleteflag = ${@com.hxht.dnsftp.model.Test@deleteflagSucc}

where id in

  #{item.id}

